import { queryAllByAttribute } from "@testing-library/react";
import React, {useState, useEffect} from "react";
import './CSS/Post.css'

const Post = (props) => {
    const post = props.data;
    let[reactions, setReactions] = useState(post.reactions);
    let[reaacted, setReaacted] = useState(post.hasReacted === 1 ? "Unlike" : "Like");
    const like = () => {
        if(reaacted === "Like"){
            alert("Like"+post.post.id);
            setReaacted("Unlike");
            setReactions(reactions+1);
        }else{
            alert("Unlike"+post.post.id);
            setReaacted("Like");
            setReactions(reactions-1);
        }
    }
    return(
        <div>
            <div className="post">
                <h4 className="card-title">{post.creator.name}</h4>
                <p className="card-text">{post.post.title}</p>
                {reactions} Reactions | {post.comments} Comments<br></br><br></br>
                <div className="reaction">
                    <button className="buttonReaction" onClick={like}>{reaacted}</button>
                    <input className="inputComment" type="text" placeholder="Comment"></input>
                    <button className="buttonComment">Comment</button>
                </div>
            </div>
        </div>
    )
}

export default Post;
