import React, {useState, useEffect} from "react";
import {Link, useNavigate,} from "react-router-dom";
import axios from "axios";
import axiosConfig from './axiosConfig';

const Home = (props) => {
    let[content, setContent] = useState([]);
    const posts = props.data;
    let navigate = useNavigate();
    const AddPost = () => {
        var obj = {title: content, content: content};
        console.log(obj);
        axiosConfig.post("posts",obj)
        .then(resp=>{
            console.log(resp.data);
            //window.location.reload(false);
        }
        ).catch(err=>{
            console.log(err);
        });
    }
    const Logout = () => {
        axiosConfig.post("logout")
        .then(resp=>{
            console.log(resp.data);
            localStorage.removeItem('user');
            window.location.reload(false);
            navigate("/");
        }).catch(err=>{
            console.log(err);
        });
    }
    return ( 
        <div>
            <h1 className="text">Welcome to NeighbourGen</h1>
            <button className="buttonLogout" onClick={Logout}>Logout</button>
            <input className="inputContent" type="text" name = {content} onChange={(e)=>setContent(e.target.value)} placeholder="......"></input><br></br>
            <button className="buttonPost" onClick={AddPost}>Add Post</button>
            <div className = "tableContentTeams">
            <h1>All posts</h1>
            <table className='rwd-table'>
                <thead>
                    <tr>
                        <th>Created By</th>
                        <th>Content</th>
                        <th>Comments</th>
                        <th>Reactions</th>
                        <th>hasReacted</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    posts.map(p=>(
                        <tr key={p.post.id}>
                            <th>{p.creator.name}</th>
                            <td>{p.post.content}</td>
                            <td>{p.comments}</td>
                            <td>{p.reactions}</td>
                            <td>{p.hasReacted}</td>
                            {/* <td>
                                <Link to={"/delete/team/"+p.id} class="btn btn-danger"> Delete</Link>  
                            </td> */}
                        </tr>
                    ))
                    }
            </tbody>
            </table>
            </div>
        </div>
    )
}

export default Home;
