import React, {useState, useEffect} from "react";
import {Link, useNavigate} from "react-router-dom";
import axios from "axios";

const Login = () => {
    let[username, setUserName] = useState("");
    let[password, setPassword] = useState("");
    let navigate = useNavigate();

    const loginSubmit= ()=>{
        var obj = {username: username, password: password};
        axios.post("http://127.0.0.1:8000/api/login",obj)
        .then(resp=>{
            var token = resp.data;
            var user = {user_Id: token.user_id, access_token:token.token};
            localStorage.setItem('user', JSON.stringify(user));
            console.log('LOGIN SUCCESS');
            navigate("/");  
        }).catch(err=>{
            console.log(err);
        });
    }  
    return(
        <div className="container" style={{margin: 60}}>
            <form>
                <label className="form-label">User ID</label>
                <input className="form-control" type="text" value={username} onChange={(e)=>setUserName(e.target.value)} />
                <label className="form-label">Password</label>
                <input className="form-control" type="password" value={password} onChange={(e)=>setPassword(e.target.value)}></input>
            </form>
                <button className= "btn btn-success btn-lg" onClick={loginSubmit}>Login</button>
                <p>Don't have any account?</p>
                <u><Link to="/registration"> Create an account</Link></u>
        </div>

    ) 
}

export default Login;
