import { isContentEditable } from "@testing-library/user-event/dist/utils";
import React, {useState, useEffect} from "react";
import { ModalTitle } from "react-bootstrap";
import {Link, useNavigate, useParams} from "react-router-dom";
import axiosConfig from './axiosConfig';
import './CSS/Post.css'

const PostDetails = () => {
    const params = useParams();
    const postID = params.id;
    let[post, setPost] = useState();
    let[reactions, setReactions] = useState();
    let[reaacted, setReaacted] = useState();
    let[hasReacted, setHasReacted] = useState();
    let[comment, setComment] = useState("");
    let[comments, setComments] = useState();
    let[user, setUser] = useState();
    let[postTitle, setPostTitle] = useState();
    let navigate = useNavigate();

    useEffect(()=>{
        axiosConfig.get("post/"+postID)
        .then(resp=>{
            console.log(resp.data);
            setPost(resp.data);
            setReactions(post.reactionCount);
            setReaacted(post.hasReacted === 1 ? "Unlike" : "Like");
            setHasReacted(post.hasReacted === 1 ? "true" : "false");
            setComments(post.commentCount);
            setUser(post.user.name);
            setPostTitle(post.post.title);
        }).catch(err=>{
            console.log(err);
        });
    },[]);

    return (
        <h1>Post ID: {postID}</h1>
    );
}

export default PostDetails;
