import React, {useState, useEffect} from "react";
import {Link, useNavigate,} from "react-router-dom";
import axiosConfig from './axiosConfig';
import './CSS/Post.css'

const Post = (props) => {
    const post = props.data;
    let[reactions, setReactions] = useState(post.reactions);
    let[reaacted, setReaacted] = useState(post.hasReacted === 1 ? "Unlike" : "Like");
    let[hasReacted, setHasReacted] = useState(post.hasReacted);
    const like = () => {
        if(reaacted === "Like"){
            setReaacted("Unlike");
            setReactions(reactions+1);
        }else{
            setReaacted("Like");
            setReactions(reactions-1);
        }
        var obj = {post_id: post.post.id, hasReacted: hasReacted};
        axiosConfig.post("reaction", obj)
            .then(resp=>{
                console.log(resp.data);
                setHasReacted(hasReacted === 1 ? 0 : 1);
            }
            ).catch(err=>{
                console.log(err);
            }
        );
    }
    return(
        <div>
            <div className="post">
                <h4 className="card-title">{post.creator.name}</h4>
                <p className="card-text">{post.post.title}</p>
                {reactions} Reactions | {post.comments} Comments<br></br><br></br>
                <div className="reaction">
                    <button className="buttonReaction" onClick={like}>{reaacted}</button>
                    <input className="inputComment" type="text" placeholder="Comment"></input>
                    <button className="buttonComment">Comment</button>
                </div>
            </div>
        </div>
    )
}

export default Post;
