import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from "react-router-dom";
import axiosConfig from './axiosConfig';
import './CSS/Post.css'
import './CSS/Home.css'

const PostDetails = () => {
    const params = useParams();
    const postID = params.id;
    let[post, setPost] = useState();
    let[reactions, setReactions] = useState(post.reactionCount);
    let[reaacted, setReaacted] = useState(post.hasReacted === 1 ? "Unlike" : "Like");
    let[hasReacted, setHasReacted] = useState(post.hasReacted === 1 ? "true" : "false");
    let[comment, setComment] = useState("");
    let[comments, setComments] = useState(post.commentCount);
    let[user, setUser] = useState(post.user.name);
    let[postTitle, setPostTitle] = useState(post.post.title);
    let[detailComments, setDetailComments] = useState([]);
    let navigate = useNavigate();

    useEffect(()=>{
        axiosConfig.get("post/"+postID)
        .then(resp=>{
            console.log(resp.data);
            setPost(resp.data);
            setReactions(post.reactionCount);
            setReaacted(post.hasReacted === 1 ? "Unlike" : "Like");
            setHasReacted(post.hasReacted === 1 ? "true" : "false");
            setComments(post.commentCount);
            setUser(post.user.name);
            setPostTitle(post.post.title);
        }).catch(err=>{
            console.log(err);
        });
    },[]);

    const like = () => {
        if(reaacted === "Like"){
            setReaacted("Unlike");
            setReactions(reactions+1);
        }else{
            setReaacted("Like");
            setReactions(reactions-1);
        }
        var obj = {post_id: postID, hasReacted: hasReacted};
        axiosConfig.post("reaction", obj)
            .then(resp=>{
                console.log(resp.data);
                setHasReacted(hasReacted === "true" ? "false" : "true");
            }
            ).catch(err=>{
                console.log(err);
            }
        );
    }
    const addComment = () => {
        var obj = {comment: comment};
        axiosConfig.post("post/"+postID, obj)
            .then(resp=>{
                console.log(resp.data);
                setComments(comments+1);
                setComment("");
            }
            ).catch(err=>{
                console.log(err);
            }
        );
    }
    const Logout = () => {
        axiosConfig.post("logout")
        .then(resp=>{
            console.log(resp.data);
            localStorage.removeItem('user');
            window.location.reload(false);
            navigate("/");
        }).catch(err=>{
            console.log(err);
        });
    }

    return (
        <div>
            <div className="header">
                <h1 className="welcome">NeighbourGen</h1>
                <button className="buttonLogout" onClick={Logout}>Logout</button>
            </div>
            <br></br><br></br>
            <div className="container">
                <div className="postContainer">
                    <div className="post">
                        <div className="postDetails">
                            <h4 className="card-title">{user}</h4>
                            <p className="card-text">{postTitle}</p>
                            {reactions} Reactions | {comments} Comments<br></br><br></br>
                        </div>
                        <div className="reaction">
                            <button className="buttonReaction" onClick={like}>{reaacted}</button>
                            <input className="inputComment" type="text" value={comment} onChange={(e)=>setComment(e.target.value)} placeholder="Write Comment"></input>
                            <button className="buttonComment" onClick={addComment}>Comment</button>
                        </div>
                    </div>
                    <h3>Comments</h3>
                </div>
            </div>
        </div>
    );
}

export default PostDetails;
