import React, {useState, useEffect} from "react";
import {Link, useNavigate,} from "react-router-dom";
import axiosConfig from './axiosConfig';
import axios from "axios";
import {useParams} from "react-router-dom";
import './CSS/Home.css'


const Home = (props) => {
    let[content, setContent] = useState([]);
    let[neighbourhoodName, setNeighbourhoodName] = useState("");
    const posts = props.data;
    console.log(posts);
    let navigate = useNavigate();

    const neighbourhoodID = posts[0].post.neighbourhood_id;
    useEffect(()=>{
        axios.get("http://127.0.0.1:8000/api/neighbourhoods")
        .then(resp=>{
            for(var i=0; i<resp.data.length; i++){
                if(resp.data[i].id === neighbourhoodID){
                    setNeighbourhoodName(resp.data[i].name);
                }
            }
        }
        ).catch(err=>{
            console.log(err);
        });
    },[]);
    const AddPost = () => {
        var obj = {title: content, content: content};
        console.log(obj);
        axiosConfig.post("home",obj)
        .then(resp=>{
            console.log(resp.data);
            window.location.reload(false);
        }
        ).catch(err=>{
            console.log(err);
        });
    }
    const Logout = () => {
        axiosConfig.post("logout")
        .then(resp=>{
            console.log(resp.data);
            localStorage.removeItem('user');
            window.location.reload(false);
            navigate("/");
        }).catch(err=>{
            console.log(err);
        });
    }
    return ( 
        <div>
            <div className="header">
                <h1 className="welcome">NeighbourGen</h1>
                <button className="buttonLogout" onClick={Logout}>Logout</button>
            </div>
            <div className="container">
                <h2 className="heading">Share your update</h2>
                <textarea className="inputContent" onChange={(e)=>setContent(e.target.value)} placeholder="Write your post" rows={3} cols={50}/><br></br>
                <button className="btn btn-success" style={{marginLeft: 10}} onClick={AddPost}>Add Post</button>

            <div className = "tableContentTeams">
            <h2>{neighbourhoodName} Updates</h2>
            <table className='rwd-table'>
                <thead>
                    <tr>
                        <th>Created By</th>
                        <th>Content</th>
                        <th>Comments</th>
                        <th>Reactions</th>
                        <th>hasReacted</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    posts.map(p=>(
                        <tr key={p.post.id}>
                            <th>{p.creator.name}</th>
                            <td>{p.post.content}</td>
                            <td>{p.comments}</td>
                            <td>{p.reactions}</td>
                            <td>{p.hasReacted}</td>
                            {/* <td>
                                <Link to={"/delete/team/"+p.id} class="btn btn-danger"> Delete</Link>  
                            </td> */}
                        </tr>
                    ))
                    }
            </tbody>
            </table>
            </div>
            </div>
        </div>
    )
}

export default Home;
