import React, {useState, useEffect} from "react";
import {Link, useNavigate} from "react-router-dom";
import axios from "axios";

const Home = (props) => {
    let[content, setContent] = useState([]);
    let history = useNavigate();
    const AddPost = () => {
        var obj = {content: content, user_id: props.data.user_id};
        console.log(obj);
        axios.post("http://127.0.0.1:8000/api/home",obj)
        .then(resp=>{
            var token = resp.data;
            console.log(token);
            history("/home");
        }).catch(err=>{
            console.log(err);
        });
    }

    const[posts, setPosts] = useState([]);
    useEffect(()=>{
        axios.get("http://127.0.0.1:8000/api/home")
        .then(resp=>{
        console.log(resp.data);
        setPosts(resp.data);
         }).catch(err=>{
        console.log(err);
    });
    },[]);
    return ( 
        <div>
            <h1 className="text">Welcome to NeighbourGen</h1>
            <input className="inputContent" type="text" name = {content} onChange={(e)=>setContent(e.target.value)} placeholder="......"></input><br></br>
            <button className="buttonPost" onClick={AddPost}>Add Team</button>
            <div className = "tableContentTeams">
            <h1>All posts</h1>
            <table className='rwd-table'>
                <thead>
                    <tr>
                        <th>Team Id</th>
                        <th>Team Name</th>
                        <th>Team Code</th>
                        <th>Created At</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    posts.map(p=>(
                        <tr>
                            <th>{p.creator.name}</th>
                            <td>{p.post.content}</td>
                            <td>{p.comments}</td>
                            <td>{p.reactions}</td>
                            <td>{p.hasReacted}</td>
                            {/* <td>
                                <Link to={"/delete/team/"+p.id} class="btn btn-danger"> Delete</Link>  
                            </td> */}
                        </tr>
                    ))
                    }
            </tbody>
            </table>
            </div>
        </div>
    )
}

export default Home;
