import React, {useState, useEffect} from "react";
import axiosConfig from './axiosConfig';
import './CSS/Home.css'
import Navigation from "./Navigation";
import Post from "./Post";

const Profile = () => {
    let[posts, setPosts] = useState([]);
    let[name, setName] = useState("");
    let[email, setEmail] = useState("");
    let[neighbourhood, setNeighbourhood] = useState("");

    useEffect(()=>{
        axiosConfig.get("profile")
        .then(resp=>{
            console.log(resp.data);
            setPosts(resp.data.postDtos);
            setName(resp.data.user.name);
            setEmail(resp.data.user.email);
            setNeighbourhood(resp.data.neighbourhood.name);
        }).catch(err=>{
            console.log(err);
        });
    },[]);

    return (
        <div>
            <Navigation />
            <div className="container">
                <h1>Profile</h1>
                <div className="profile">
                    <h3>Name: {name}</h3>
                    <h3>Email: {email}</h3>
                    <h3>Neighbourhood: {neighbourhood}</h3>
                </div>
                <h2>Your posts</h2><br></br>
                {[...posts].reverse().map(p=>(
                    <div className="postContainer" key={p.post.id}>
                        <Post data={p}/>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Profile;
